import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import 'swiper/css/navigation'; 
import { Navigation } from 'swiper/modules';

const colors = ["#FF5733", "#33FF57", "#3357FF", "#FF33A1", "#A133FF", "#33FFF2", "#FFC733", "#8D33FF", "#FF3368"];

const slides = Array.from({ length: 10 }, (_, index) => ({
  id: index,
  name: "Red Apple",
  image: "https://wp.alithemes.com/html/nest/demo/assets/imgs/shop/cat-9.png",
  color: colors[Math.floor(Math.random() * colors.length)] // Assigning a random color
}));

const HomeCat = () => {
  return (
    <section className="homeCat">
      <div className="container">
        <Swiper
          slidesPerView={5}
          spaceBetween={10}
          navigation={true}
          slidesPerGroup={3}
          pagination={false}
          modules={[Navigation]}
          className="mySwiper"
        >
          {slides.map((slide) => (
            <SwiperSlide key={slide.id}>
              <div className="item text-center" style={{ backgroundColor: slide.color }}>
                <img src={slide.image} alt={slide.name} />
                <h6>{slide.name}</h6>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </section>
  );
};

export default HomeCat;
