import FormGroup from "@mui/material/FormGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import ReactSlider from "react-range-slider-input";
import "react-range-slider-input/dist/style.css";
import { useState } from "react";

const Slidebar = () => {
  const [value, setValue] = useState([100, 60000]);

  return (
    <div className="slidebar">
      {/* Product Categories */}
      <div className="filterBox">
        <h6>PRODUCT CATEGORIES</h6>
        <div className="scroll">
          <ul>
            {["Men", "Women", "Watches", "Lifestyle", "Kids", "Gifts", "Beauty"].map((category) => (
              <li key={category}>
                <FormControlLabel className="w-100" control={<Checkbox />} label={category} />
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Price Filter with Slider */}
      <div className="filterBox">
        <h6>FILTER BY PRICE</h6>
        <div className="price-range">
          <ReactSlider
            min={0}
            max={100000}
            step={100}
            value={value}
            onInput={setValue} // Updates state when the slider moves
          />
          <div className="price-values">
            <span>₹{value[0]}</span> - <span>₹{value[1]}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Slidebar;
