import { MyContext } from "../../App";
import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { FaGoogle } from "react-icons/fa";

const SignIn = () => {
  const context = useContext(MyContext);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  useEffect(() => {
    context.setisHeaderFooterShow(false);

    return () => {
      context.setisHeaderFooterShow(true);
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Login attempt with:", email, password, rememberMe);
  };

  return (
    <section 
      className="d-flex align-items-center justify-content-center" 
      style={{ 
        backgroundColor: "#F5F5DC", 
        minHeight: "100vh", 
        width: "100vw", 
        padding: "20px"
      }}
    >
      <div style={{ maxWidth: "400px", width: "100%" }}>
        <div className="text-center mb-3">
          <h2 className="fw-bold">Sign in</h2>
          <p className="mb-0">or <Link to="/signup" className="text-decoration-none">create an account</Link></p>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <input 
              type="email" 
              className="form-control form-control-lg" 
              id="email" 
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{ padding: "12px", borderRadius: "4px", height: "auto" }}
            />
          </div>

          <div className="mb-3">
            <input 
              type="password" 
              className="form-control form-control-lg" 
              id="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{ padding: "12px", borderRadius: "4px", height: "auto" }}
            />
          </div>

          <div className="mb-3">
            <div className="form-check">
              <input 
                className="form-check-input" 
                type="checkbox" 
                id="rememberMe"
                checked={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)}
                style={{ 
                  width: "18px", 
                  height: "18px", 
                  backgroundColor: rememberMe ? "#0d6efd" : "white",
                  borderColor: "#0d6efd"
                }}
              />
              <label className="form-check-label" htmlFor="rememberMe">
                Remember me
              </label>
            </div>
          </div>

          <div className="d-grid mb-3">
            <button 
              type="submit" 
              className="btn btn-primary btn-lg"
              style={{ backgroundColor: "#0d6efd", padding: "12px", borderRadius: "4px" }}
            >
              Sign in
            </button>
          </div>
        </form>

        <div className="d-grid mb-3">
          <button 
            className="btn btn-outline-secondary btn-lg d-flex align-items-center justify-content-center"
            style={{ padding: "12px", borderRadius: "4px", backgroundColor: "white" }}
          >
            <img 
              src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg" 
              alt="Google logo" 
              style={{ width: "18px", height: "18px", marginRight: "10px" }}
            />
            Sign in with Google
          </button>
        </div>

        <div className="text-center">
          <Link to="/forgot-password" className="text-decoration-none">
            Forgotten your password?
          </Link>
        </div>
      </div>
    </section>
  );
}

export default SignIn;
