import { Button } from "@mui/material";
import { IoIosMenu } from "react-icons/io";
import { FaAngleDown } from "react-icons/fa6";
import { Link } from "react-router-dom";
import { useState } from "react"; // Import useState

const Navigation = () => {
  const [showAllCategories, setShowAllCategories] = useState(false);

  const toggleCategories = () => {
    setShowAllCategories(!showAllCategories);
  };

  return (
    <nav>
      <div className="container">
        <div className="row">
          <div className="col-sm-3 navPart1">
            <div className="catWrapper">
              <Button
                className="allCatTab align-items-center"
                onClick={toggleCategories}
              >
                <span className="icon1 mr-2"><IoIosMenu /></span>
                <span className="text">ALL CATEGORIES</span>
                <span className="icon2 ml-2"><FaAngleDown /></span>
              </Button>

              {/* Show categories dropdown if showAllCategories is true */}
              {showAllCategories && (
                <div className="all-categories-dropdown">
                  <ul className="list list-inline">
                    <li><Link to="">Electronics</Link></li>
                    <li><Link to="">Furniture</Link></li>
                    <li><Link to="">Books</Link></li>
                    <li><Link to="">Sports</Link></li>
                    <li><Link to="">Clothing</Link></li>
                    <li><Link to="">Toys</Link></li>
                    <li><Link to="">Beauty</Link></li>
                    <li><Link to="">Home Appliances</Link></li>
                  </ul>
                </div>
              )}
            </div>
          </div>
          <div className="col-sm-9 navPart2">
            <ul className="list list-inline">
              <li className="list-inline-item">
                <Link to="">Home</Link>
              </li>
              <li className="list-inline-item">
                <Link to="">Blog</Link>
              </li>
              <li className="list-inline-item">
                <Link to="">Contact Us</Link>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
