import React from "react";
import { Button } from "@mui/material";
import HomeBanner from "../../Components/HomeBanner";
import banner1 from "../../assets/images/banner1.jpg";
import { HiArrowLongRight } from "react-icons/hi2";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css"; 

const Home = () => {
  // Slider settings
  var productSliderOptions = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4, // Show 4 images at once
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024, 
        settings: {
          slidesToShow: 3,
        },
      },
      {
        breakpoint: 768, 
        settings: {
          slidesToShow: 2,
        },
      },
      {
        breakpoint: 480, 
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  // Dummy product images
  const dummyProducts = [
    "https://via.placeholder.com/200x200?text=Product+1",
    "https://via.placeholder.com/200x200?text=Product+2",
    "https://via.placeholder.com/200x200?text=Product+3",
    "https://via.placeholder.com/200x200?text=Product+4",
    "https://via.placeholder.com/200x200?text=Product+5",
    "https://via.placeholder.com/200x200?text=Product+6",
  ];

  return (
    <>
      <HomeBanner />
      <section className="homeProducts">
        <div className="container">
          <div className="row">
            {/* Left Column - Image */}
            <div className="col-md-3">
              <div className="banner">
                <img 
                  src={banner1} 
                  className="cursor"  
                  style={{ width: "100%", height: "auto" }} 
                  alt="Banner"
                />
              </div>
            </div>

            {/* Right Column - Content & Button */}
            <div className="col-md-9 productRow">
              <div className="d-flex align-items-center justify-content-between w-100">
                <div className="info">
                  <h3>BEST SELLER</h3>
                  <p style={{ color: "#111" }}>
                    Do not miss the current offers until the end of March
                  </p>
                </div>

                {/* Button Container - Moves Button to Right */}
                <div className="d-flex justify-content-end">
                  <Button className="viewAllBtn">
                    View All <HiArrowLongRight />
                  </Button>
                </div>
              </div>

              {/* Product Slider */}
              <div className="product_row mt-4">
                <Slider {...productSliderOptions}>
                  {dummyProducts.map((image, index) => (
                    <div key={index} className="item productItem">
                      <img src={image} alt={`Product ${index + 1}`} className="product-img"/>
                    </div>
                  ))}
                </Slider>
              </div>

            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Home;
