import React, { useState } from "react";
import Slidebar from "../../Components/Slidebar";
import { Link } from 'react-router-dom';
import Button from '@mui/material/Button';
import { IoIosMenu } from "react-icons/io";
import { TbGridDots } from "react-icons/tb";
import { HiViewGrid } from "react-icons/hi";
import { TfiLayoutGrid3Alt } from "react-icons/tfi";
import { FaAngleDown } from "react-icons/fa6";
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';

const Listing = () => {
  // Ensure useState is correctly initialized
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl); // Boolean() prevents errors

  const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <>
      <section className="product_Listing_Page">
        <div className="container">
          <div className="productListing d-flex">
            <Slidebar />
            <div className="content_right">
              <Link to="#">
                <img 
                  src="https://img.freepik.com/premium-vector/premium-gazelnut-chocolate-bar-with-sauce-pouring-down-from-top-golden-glitter-background-3d-illustration_317810-2552.jpg?w=2000" 
                  className="w-100"  
                  alt="Product"
                />
              </Link>
              <div className="showBy mt-3 mb-3 d-flex align-items-center">
                <div className="btnWrapper d-flex align-items-center">
                  <Button><IoIosMenu/></Button>
                  <Button><HiViewGrid/></Button>
                  <Button><TbGridDots/></Button>
                  <Button><TfiLayoutGrid3Alt/></Button>
                </div>
                <div className="ms-auto showByFliter">
                  <Button onClick={handleClick}>
                    Show 9 <FaAngleDown />
                  </Button>
                  <Menu
                    id="basic-menu"
                    anchorEl={anchorEl}
                    open={open}  // ✅ Correctly handled state
                    onClose={handleClose}
                    MenuListProps={{
                      'aria-labelledby': 'basic-button',
                    }}
                  >
                    <MenuItem onClick={handleClose}>Profile</MenuItem>
                    <MenuItem onClick={handleClose}>My account</MenuItem>
                    <MenuItem onClick={handleClose}>Logout</MenuItem>
                  </Menu>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

export default Listing;
